var title_f14_54_15212="Air embolism lab findings";
var content_f14_54_15212=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F81655&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F81655&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Laboratory findings associated with air emboli",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Electrocardiographic changes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sinus tachycardia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Right heart strain (peaked P-wave)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Non-specific ST-segment and T-wave changes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acute myocardial ischemia or infarction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Radiographic findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normal (most common finding)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary edema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Other findings rarely reported include:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Air in the main pulmonary artery*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Focal oligemia (especially upper lobes)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Pulmonary artery enlargement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Atelectasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Intracardiac air",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Air in the hepatic circulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Arterial blood gas abnormalities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypoxemia (may be severe)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypercarbia (increased dead space)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Hemodynamic changes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increase in central venous pressure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increase in pulmonary artery pressures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increase in right ventricular pressure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fall in cardiac output",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fall in mean arterial pressure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Serological findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fall in platelet count",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Although this finding is only rarely reported, it is pathognomonic for air embolism.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
