var title_f14_54_15213="TNM staging RCC";
var content_f14_54_15213=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F54563&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F54563&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    TNM staging system for kidney cancer",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Primary tumor (T)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       TX",
"      </td>",
"      <td colspan=\"3\">",
"       Primary tumor cannot be assessed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T0",
"      </td>",
"      <td colspan=\"3\">",
"       No evidence of primary tumor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       T1",
"      </td>",
"      <td class=\"sublist_other_start\" colspan=\"3\">",
"       Tumor 7 cm or less in greatest dimension, limited to the kidney",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       T1a",
"      </td>",
"      <td class=\"sublist_other\" colspan=\"3\">",
"       Tumor 4 cm or less in greatest dimension, limited to the kidney",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       T1b",
"      </td>",
"      <td class=\"sublist_other\" colspan=\"3\">",
"       Tumor more than 4 cm but not more than 7 cm in greatest dimension, and limited to the kidney",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       T2",
"      </td>",
"      <td class=\"sublist_other_start\" colspan=\"3\">",
"       Tumor more than 7 cm in greatest dimension, limited to the kidney",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       T2a",
"      </td>",
"      <td class=\"sublist_other\" colspan=\"3\">",
"       Tumor more than 7 cm but less than or equal to 10 cm in greatest dimension, limited to the kidney",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       T2b",
"      </td>",
"      <td class=\"sublist_other\" colspan=\"3\">",
"       Tumor more than 10 cm, limited to the kidney",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       T3",
"      </td>",
"      <td class=\"sublist_other_start\" colspan=\"3\">",
"       Tumor extends into major veins or perinephric tissues but not into the ipsilateral adrenal gland and not beyond Gerota's fascia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       T3a",
"      </td>",
"      <td class=\"sublist_other\" colspan=\"3\">",
"       Tumor grossly extends into the renal vein or its segmental (muscle containing) branches, or tumor invades perirenal and/or renal sinus fat but not beyond Gerota's fascia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       T3b",
"      </td>",
"      <td class=\"sublist_other\" colspan=\"3\">",
"       Tumor grossly extends into the vena cava below the diaphragm",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       T3c",
"      </td>",
"      <td class=\"sublist_other\" colspan=\"3\">",
"       Tumor grossly extends into the vena cava above the diaphragm or invades the wall of the vena cava",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T4",
"      </td>",
"      <td colspan=\"3\">",
"       Tumor invades beyond Gerota's fascia (including contiguous extension into the ipsilateral adrenal gland)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Regional lymph nodes (N)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       NX",
"      </td>",
"      <td colspan=\"3\">",
"       Regional lymph nodes cannot be assessed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       N0",
"      </td>",
"      <td colspan=\"3\">",
"       No regional lymph node metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       N1",
"      </td>",
"      <td colspan=\"3\">",
"       Metastasis in regional lymph node(s)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Distant metastasis (M)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       M0",
"      </td>",
"      <td colspan=\"3\">",
"       No distant metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       MI",
"      </td>",
"      <td colspan=\"3\">",
"       Distant metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Anatomic stage/prognostic groups",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage I",
"      </td>",
"      <td>",
"       T1",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage II",
"      </td>",
"      <td>",
"       T2",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Stage III",
"      </td>",
"      <td>",
"       T1 or T2",
"      </td>",
"      <td>",
"       N1",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T3",
"      </td>",
"      <td>",
"       N0 or N1",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Stage IV",
"      </td>",
"      <td>",
"       T4",
"      </td>",
"      <td>",
"       Any N",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Any T",
"      </td>",
"      <td>",
"       Any N",
"      </td>",
"      <td>",
"       M1",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Note: cTNM is the clinical classification, pTNM is the pathologic classification.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Used with the permission of the American Joint Committee on Cancer (AJCC), Chicago, Illinois. The original source for this material is the AJCC Cancer Staging Manual, Seventh Edition (2010) published by Springer New York, Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
