var title_f14_55_15228="Lead chelating agents B";
var content_f14_55_15228=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F71832&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F71832&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Characteristic features and adverse effects of chelating agents used in the treatment of childhood lead poisoning",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Agent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        CaNa",
"        <sub>",
"         2",
"        </sub>",
"        EDTA",
"       </td>",
"       <td>",
"        Only removes lead from the extracellular compartment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May increase CNS lead burden",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Must be given after BAL has been administered in patients with severe lead intoxication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May cause hypercalcemia, renal dysfunction, excretion of divalent minerals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"5\">",
"        BAL*",
"       </td>",
"       <td>",
"        Must be administered intramuscularly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Can be administered in the presence of renal failure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Contraindicated in individuals with peanut allergy or hepatic insufficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May cause hemolysis in individuals with G6PD deficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Causes vomiting if administered with iron",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"6\">",
"        D-penicillamine",
"       </td>",
"       <td>",
"        Not FDA approved for treatment of lead poisoning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administered orally",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Absorption impaired by iron, antacids, and food",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Contraindicated in penicillin allergy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Avoid giving with dairy products or iron",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May cause rash, nausea, vomiting, leukopenia, thrombocytopenia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        DMSA",
"       </td>",
"       <td>",
"        FDA approved for treatment of lead poisoning in children with BLL &gt;45 &micro;g/dL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administered orally",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May cause elevated LFT's",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May cause hemolysis in individuals with G6PD deficiency",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * British anti-Lewisite.",
"    </div>",
"    <div class=\"reference\">",
"     file://www.atsdr.cdc.gov/HEC/CSEM/lead/treatment_management.html#Table2",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
