var title_f14_56_15243="Spinal cord injury grading scale";
var content_f14_56_15243=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F60383&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F60383&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Grading scales for spinal cord injury",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        American Spinal Injury Association Scale (ASIA)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A",
"       </td>",
"       <td>",
"        No motor or sensory function is preserved below the neurologic level through the sacral segments S4-5 (complete cord injury)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B",
"       </td>",
"       <td>",
"        Sensory but not motor function is preserved below the neurologic level and extends through the sacral segments S4-5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C",
"       </td>",
"       <td>",
"        Motor function is preserved below the neurologic level and the majority of key muscles below the neurologic level have a muscle grade less than 3 (ie, no antigravity movement)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        D",
"       </td>",
"       <td>",
"        Motor function is preserved below the neurologic level and the majority of key muscles below the neurologic level have a muscle grade of at least 3 (at least antigravity movement)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        E",
"       </td>",
"       <td>",
"        Motor and sensory functions are normal (no cord injury)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
