var title_f14_57_15259="Rx genital warts";
var content_f14_57_15259=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F74829&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F74829&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment options for genital warts*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Drugs of choice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Trichloroacetic or bichloroacetic acid, or podophyllin&bull; or liquid nitrogen one to two times weekly until resolved",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Imiquimod 5 percent&bull; three times weekly for 16 weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Podofilox 0.5 percent&bull; twice daily for three days, four days rest, then repeated up to four times",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Alternatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Surgical removal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Laser surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intralesional interferon",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * The above recommendations are only for external genital warts. Liquid nitrogen can also be used for vaginal, urethral, and oral warts. Podofilox or imiquimod can be used for urethral meatus warts. Trichloroacetic or bichloroacetic acid can be used for anal warts.",
"     <br>",
"      &bull; Not recommended in pregnancy.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Drugs for sexually transmitted diseases. Treat Guidel Med Lett 2004; 2:67.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
