var title_f14_57_15260="Poser criteria for MS";
var content_f14_57_15260=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F62382&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F62382&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Poser diagnostic criteria for multiple sclerosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Clinically definite multiple sclerosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Two attacks plus two separate clinical lesions*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        or",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Two attacks plus one clinical lesion plus another separate paraclinical lesion",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Laboratory supported definite multiple sclerosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Two attacks plus one clinical lesion plus CSF, OCB, or IgG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        or",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Two attacks plus one paraclinical lesion plus CSF, OCB, or IgG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        or",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. One attack plus two separate clinical lesions plus CSF, OCB, or IgG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        or",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4. One attack plus one clinical lesion plus one paraclinical lesion plus CSF, OCB, or IgG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Clincally probably multiple sclerosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Two attacks plus one clinical lesion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        or",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. One attack plus two separate clinical lesions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        or",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3. One attack plus one clinical lesion plus one paraclinical lesion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Laboratory supported probable multiple sclerosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. Two attacks plus CSF, OCB, or IgG",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CSF: cerebrospinal fluid; OCB: oligoclonal banding; IgG: Immunoglobulin G.",
"     <br>",
"      * Clinical lesion: lesion detected on history and physical examination.",
"      <br>",
"       <span class=\"bullet\">",
"        &bull;",
"       </span>",
"       Paraclinical lesion: lesion detected by evoked response testing, MRI, or urodynamic studies.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Modified from: Poser CM, Paty DW, Scheinberg L, et al, Ann Neurol 1983; 13:227.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
