var title_f14_59_15291="USPSTF evidence grade";
var content_f14_59_15291=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F73780&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F73780&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    USPSTF grading scheme for quality of evidence",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Good:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evidence includes consistent results",
"from well-designed, well-conducted studies in representative",
"populations that directly assess effects on health outcomes.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Fair:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evidence is sufficient to determine",
"effects on health outcomes, but the strength of the evidence is limited",
"by the number, quality, or consistency of the individual studies, generalizability to routine",
"practice, or indirect nature of the evidence on health outcomes.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Poor:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evidence is insufficient to assess",
"the effects on health outcomes because of limited number or power of",
"studies, important flaws in their design or conduct, gaps in the chain of evidence, or lack of information on",
"important health outcomes.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    The USPSTF grades the quality of the overall evidence for a service on a 3-point scale (good, fair, poor).",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     From U.S. Preventive Services Task Force Ratings: Strength of Recommendations and Quality of Evidence. Guide to Clinical Preventive Services, Third Edition: Periodic Updates, 2000-2003. Agency for Healthcare Research and Quality, Rockville, MD. file://www.ahrq.gov/clinic/3rduspstf/ratings.htm.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
