var title_f14_6_14445="Developmental issues diabetes";
var content_f14_6_14445=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F53457&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F53457&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Major developmental issues and their effect on diabetes in children and adolescents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Developmental stage (approximate ages)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Normal developmental tasks",
"       </td>",
"       <td class=\"subtitle1\">",
"        Type 1 diabetes management priorities",
"       </td>",
"       <td class=\"subtitle1\">",
"        Family issues in type 1 diabetes management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Infancy (0-12 months)",
"       </td>",
"       <td>",
"        Developing a trusting relationship/\"bonding\" with primary caregiver(s)",
"       </td>",
"       <td>",
"        <p>",
"         Preventing and treating hypoglycemia",
"        </p>",
"        <p>",
"         Avoiding extreme fluctuations in blood glucose levels",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Coping with stress",
"        </p>",
"        <p>",
"         Sharing the \"burden of care\" to avoid parent burnout",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Toddler (13-36 months)",
"       </td>",
"       <td>",
"        Developing a sense of mastery and autonomy",
"       </td>",
"       <td>",
"        <p>",
"         Preventing and treating hypoglycemia",
"        </p>",
"        <p>",
"         Avoiding extreme fluctuations in blood glucose levels due to irregular food intake",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Establishing a schedule",
"        </p>",
"        <p>",
"         Managing the \"picky eater\" Setting limits and coping with toddler's lack of cooperation with regimen Sharing the burden of care",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Preschooler and early elementary school-age (3-7 years)",
"       </td>",
"       <td>",
"        Developing initiative in activities and confidence in self",
"       </td>",
"       <td>",
"        <p>",
"         Preventing and treating hypoglycemia",
"        </p>",
"        <p>",
"         Unpredictable appetite and activity",
"        </p>",
"        <p>",
"         Positive reinforcement for cooperation with regimen",
"        </p>",
"        <p>",
"         Trusting other caregivers with diabetes management",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Reassuring child that diabetes is no one's fault",
"        </p>",
"        <p>",
"         Educating other caregivers about diabetes management",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Older elementary school-age (8-11 years)",
"       </td>",
"       <td>",
"        <p>",
"         Developing skills in athletic, cognitive, artistic, social areas",
"        </p>",
"        <p>",
"         Consolidating self-esteem with respect to the peer group",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Making diabetes regimen flexible to allow for participation in school/peer activities",
"        </p>",
"        <p>",
"         Child learning short- and long-term benefits of optimal control",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Maintaining parental involvement in insulin and blood glucose monitoring tasks while allowing for independent self-care for \"special occasions\"",
"        </p>",
"        <p>",
"         Continue to educate school and other caregivers",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Early adolescence (12-15 years)",
"       </td>",
"       <td>",
"        <p>",
"         Managing body changes",
"        </p>",
"        <p>",
"         Developing a strong sense of self-identity",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Managing increased insulin requirements during puberty",
"        </p>",
"        <p>",
"         Diabetes management and blood glucose control become more difficult",
"        </p>",
"        <p>",
"         Weight and body image concerns",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Renegotiating parents and teen's roles in diabetes management to be acceptable to both",
"        </p>",
"        <p>",
"         Learning coping skills to enhance ability to self-manage",
"        </p>",
"        <p>",
"         Preventing and interventing with diabetes-related family conflict",
"        </p>",
"        <p>",
"         Monitoring for signs of depression, earing disorders, risky behaviors",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Later adolescence (16-19 years)",
"       </td>",
"       <td>",
"        Establishing a sense of identity after high school (decision about location, social issues, work, education)",
"       </td>",
"       <td>",
"        <p>",
"         Begin discussion of transition to a new diabetes team",
"        </p>",
"        <p>",
"         Integrating diabetes into new lifestyle",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Supporting the transition to independence Learning coping skills to enhance ability to self-manage",
"        </p>",
"        <p>",
"         Preventing and intervening with diabetes-related family conflict",
"        </p>",
"        <p>",
"         Monitoring for signs of depression, eating disorders, risky behaviors",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reprinted from: Silverstein, J, Klingensmith, G, Copeland, K. Care of Children and Adolescents with Type 1 Diabetes. Diabetes Care 2005; 28:186. Copyright &copy;2005 The American Diabetes Association.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
