var title_f14_61_15323="Irritant patch test reactions";
var content_f14_61_15323=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F57968&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F57968&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Irritant patch test reactions",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Erythematous",
"       </td>",
"       <td>",
"        Characterized by sharp cut-offs at the patch test site. Seen with fragrance mix, carbamate mix.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Follicular",
"       </td>",
"       <td>",
"        Follicular papules as opposed to micro vesicles. Seen with nickel, cobalt.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pustular",
"       </td>",
"       <td>",
"        More common in atopic individuals",
"        <sup>",
"         [1]",
"        </sup>",
"        . Can be seen with metal salts (eg, nickel). Occasionally can be seen with true allergic reactions.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Soap",
"       </td>",
"       <td>",
"        Glazed erythematous appearance. Seen when testing soaps and shampoos.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bullous/necrotic",
"       </td>",
"       <td>",
"        Seen with high concentrations of caustic materials including gasoline, kerosene.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br>",
"      <ol>",
"       <li>",
"        Hjorth N. Diagnostic patch testing. In: Dermatoxicology and pharmacology, Marzulli F, Maibach HI (Eds), John Wiley and Sons, New York 1977. p.344.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
