var title_f14_63_15356="Liver injury scale";
var content_f14_63_15356=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F53232&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F53232&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Liver injury scale (1994 revision)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Grade*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Injury type",
"       </td>",
"       <td class=\"subtitle1\">",
"        Injury description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        I",
"       </td>",
"       <td>",
"        Hematoma",
"       </td>",
"       <td>",
"        Subcapsular, &lt;10 percent surface area",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Laceration",
"       </td>",
"       <td>",
"        Capsular tear, &lt;1 cm parenchymal depth",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"3\">",
"        II",
"       </td>",
"       <td rowspan=\"2\">",
"        Hematoma",
"       </td>",
"       <td>",
"        Subcapsular, 10-50 percent surface area",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intraparenchymal, &lt;10 cm in diameter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Laceration",
"       </td>",
"       <td>",
"        1-3 cm parenchymal depth, &lt;10 cm in length",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"3\">",
"        III",
"       </td>",
"       <td>",
"        Hematoma",
"       </td>",
"       <td>",
"        Subcapsular, &gt;50 percent surface area or expanding; ruptured subcapsular or parenchymal hematoma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Laceration",
"       </td>",
"       <td>",
"        Intraparenchymal hematoma &gt;10 cm or expanding",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &gt;3 cm parenchymal depth",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        IV",
"       </td>",
"       <td>",
"        Laceration",
"       </td>",
"       <td>",
"        Parenchymal disruption involving 25-75 percent of hepatic lobe or 1-3 Couinaud's segments within a single lobe",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        V",
"       </td>",
"       <td>",
"        Laceration",
"       </td>",
"       <td>",
"        Parenchymal disruption involving &gt;75 percent of hepatic lobe or &gt;3 Couinaud's segments within a single lobe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vascular",
"       </td>",
"       <td>",
"        Juxtahepatic venous injuries; ie, retrohepatic vena cava/central major hepatic veins",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        VI",
"       </td>",
"       <td>",
"        Vascular",
"       </td>",
"       <td>",
"        Hepatic avulsion",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Advance one grade for multiple injuries, up to grade III.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Moore, EE, Cogbill, TH, Jurkovich, GJ, et al. Organ injury scaling: spleen and liver (1994 revision). J Trauma 1995; 38:323.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
