var title_f14_63_15357="Microcephaly deletion and duplication syndromes";
var content_f14_63_15357=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F56397&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F56397&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Selected microdeletion and microduplication syndromes that can feature microcephaly*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Chromosomal location",
"       </td>",
"       <td class=\"subtitle1\">",
"        Features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Selected reference(s)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1q21.1 deletion",
"       </td>",
"       <td>",
"        Moderate/mild learning difficulties, heart defects, cataract (1q21.1 duplications associated with macrocephaly)",
"       </td>",
"       <td>",
"        1,2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2q23.1 deletion",
"       </td>",
"       <td>",
"        Severe learning and communication problems, seizures, stereotypies, disturbed sleep",
"       </td>",
"       <td>",
"        3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2q31.1 deletion",
"       </td>",
"       <td>",
"        Learning difficulties, growth retardation, seizures, microcephaly in 50 percent, short palpebral fissures, broad eyebrows",
"       </td>",
"       <td>",
"        4",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5q35 microduplication (reciprocal to the Sotos syndrome/NSD1 common deletion)",
"       </td>",
"       <td>",
"        Short stature, delayed bone age, speech delay, and mild or no dysmorphism (opposite of Sotos syndrome?)",
"       </td>",
"       <td>",
"        5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        6q25 deletion",
"       </td>",
"       <td>",
"        Callosal agenesis, facial dysmorphisms, hearing loss",
"       </td>",
"       <td>",
"        6",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        9q34 deletion",
"       </td>",
"       <td>",
"        Mental retardation, minor facial dysmorphisms, epilepsy; EHMT1 haploinsufficiency implicated",
"       </td>",
"       <td>",
"        7",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        16p11.2 deletion and duplication",
"       </td>",
"       <td>",
"        Autism and macrocephaly with deletion 16p11.2;&nbsp;attention deficit hyperactivity&nbsp;and microcephaly with duplication 16p11.2; epilepsy",
"       </td>",
"       <td>",
"        8",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        17q23.1q23.2 microdeletion",
"       </td>",
"       <td>",
"        Speech delay, microcephaly, growth retardation, dysmorphisms (heart, hands, limbs), abnormalities",
"       </td>",
"       <td>",
"        9",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        22q13.3 deletion",
"       </td>",
"       <td>",
"        Severe mental retardation and speech deficits, minimal dysmorphism (SHANK3 and other genes implicated)",
"       </td>",
"       <td>",
"        10",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Note that the clinical findings other than microcephaly often are unremarkable, so array CGH is usually required for diagnosis. Additional information on emerging genomic disorders is available at:",
"     <a href=\"https://decipher.sanger.ac.uk/application/syndrome\">",
"      https://decipher.sanger.ac.uk/application/syndrome",
"     </a>",
"     .",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br>",
"      <ol>",
"       <li>",
"        Mefford HC, Sharp AJ, Baker C, et al. Recurrent rearrangements of chromosome 1q21.1 and variable pediatric phenotypes. N Engl J Med 2008; 359:1685.",
"       </li>",
"       <li>",
"        Brunetti-Pierri N, Berg JS, Scaglia F, et al. Recurrent reciprocal 1q21.1 deletions and duplications associated with microcephaly or macrocephaly and developmental and behavioral abnormalities. Nat Genet 2008; 40:1466.",
"       </li>",
"       <li>",
"        van Bon BW, Koolen DA, Brueton L, et al. The 2q23.1 microdeletion syndrome: clinical and behavioural phenotype. Eur J Hum Genet 2010; 18:163.",
"       </li>",
"       <li>",
"        Mitter D, Chiaie BD, L&uuml;decke HJ, et al. Genotype-phenotype correlation in eight new patients with a deletion encompassing 2q31.1. Am J Med Genet A 2010; 152A:1213.",
"       </li>",
"       <li>",
"        Franco LM, de Ravel T, Graham BH, et al. A syndrome of short stature, microcephaly and speech delay is associated with duplications reciprocal to the common Sotos syndrome deletion. Eur J Hum Genet 2010; 18:258.",
"       </li>",
"       <li>",
"        Kleefstra T, van Zelst-Stams WA, Nillesen WM, et al. Further clinical and molecular delineation of the 9q subtelomeric deletion syndrome supports a major contribution of EHMT1 haploinsufficiency to the core phenotype. J Med Genet 2009; 46:598.",
"       </li>",
"       <li>",
"        Nagamani SC, Erez A, Eng C, et al. Interstitial deletion of 6q25.2-q25.3: a novel microdeletion syndrome associated with microcephaly, developmental delay, dysmorphic features and hearing loss. Eur J Hum Genet 2009; 17:573.",
"       </li>",
"       <li>",
"        Shinawi M, Liu P, Kang SH, et al. Recurrent reciprocal 16p11.2 rearrangements associated with global developmental delay, behavioral problems, dysmorphism, epilepsy, and abnormal head size. J Med Genet 2010; 47:332.",
"       </li>",
"       <li>",
"        Ballif BC, Theisen A, Rosenfeld JA, et al. Identification of a recurrent microdeletion at 17q23.1q23.2 flanked by segmental duplications associated with heart defects and limb abnormalities. Am J Hum Genet 2010; 86:454.",
"       </li>",
"       <li>",
"        Wilson HL, Crolla JA, Walker D, et al. Interstitial 22q13 deletions: genes other than SHANK3 have major effects on cognitive and language development. Eur J Hum Genet 2008; 16:1301.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
