" <!-- TC:TOPIC_PAGE -->",
" <div id=\"topicContent\">",
"  <div id=\"drugTitle\">",
"   Cefoperazone and sulbactam: International drug information",
"  </div>",
"  <div id=\"lexiTitleImg\">",
"   <img height=\"17\" src=\"./../images/lexiComp/Lexicomp_2012_71x17.myextg\" width=\"71\"/>",
"  </div>",
"  <div class=\"clear\">",
"  </div>",
"  <div id=\"drugCopy\">",
"   Copyright 1978-2013 Lexicomp, Inc. All rights reserved.",
"  </div>",
