" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Recurrent pyogenic cholangitis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/8/14472/contributors\">",
