var script_f14_9_14481=[" /* <![CDATA[ */",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function PeakExpFlow_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.PeakExpFlow_form){",
"",
"",
"doCalc = true;",
"if (Height_param.value.indexOf(',') >= 0){ Height_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Height_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Height_unit.options[Height_unit.selectedIndex].value.split('|');",
"Height = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Age_param.value.indexOf(',') >= 0){ Age_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Age_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Age_unit.options[Age_unit.selectedIndex].value.split('|');",
"Age = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = 0;",
"PEF_Female =  eTo((0.376 * ln(Age)) - (0.012 * Age) - (58.8 / Height) + 5.63);",
"",
"unit_parts = PEF_Female_unit.options[PEF_Female_unit.selectedIndex].value.split('|');",
"if (doCalc) PEF_Female_param.value = fixDP((PEF_Female - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"PEF_Male =  eTo((0.544 * ln(Age)) - (0.0151 * Age) - (74.7 / Height) + 5.48);",
"",
"unit_parts = PEF_Male_unit.options[PEF_Male_unit.selectedIndex].value.split('|');",
"if (doCalc) PEF_Male_param.value = fixDP((PEF_Male - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.PeakExpFlow_form){",
"",
"if (Age_param.value && Age < 15) {",
"Age = 0;",
"Age_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Age is 15 yr.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Age_param.value && Age > 85) {",
"Age_param.value = \"\";",
"clrResults();",
"Age = 0;",
"doCalc = false;",
"alert(\"The maximum value for Age is 85 yr.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.PeakExpFlow_form){",
"",
"PEF_Female_param.value = '';",
"PEF_Male_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"/* ]]> */"].join("\n");
