" <!-- TC:TOPIC_PAGE -->",
" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Occupational asthma: Management, prognosis, and prevention",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/9/14489/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/9/14489/contributors\">",
"     Moira Chan-Yeung, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/9/14489/contributors\">",
"     Jean-Luc Malo, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/9/14489/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/9/14489/contributors\">",
"     Peter J Barnes, DM, DSc, FRCP, FRS",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?14/9/14489/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?14/9/14489/contributors\">",
"     Helen Hollingsworth, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"UTD.htm?14/9/14489/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
"  <div id=\"reviewProcess\">",
"   <span>",
"    All topics are updated as new evidence becomes available and our",
"   </span>",
"   <a href=\"/home/editorial-policy\" target=\"_blank\">",
"    peer review process",
"   </a>",
"   <span>",
"    is complete.",
"   </span>",
"  </div>",
"  <div id=\"literatureReviewDate\">",
"   <span class=\"emphasis\">",
"    Literature review current through:",
"   </span>",
"   Oct 2013.",
"   <span class=\"pipeSpace\">",
