var title_f15_1_15387="Diagnostic criteria for obsessive-compulsive disorder";
var content_f15_1_15387=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F78513&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F78513&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for obsessive-compulsive disorder",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        1. Either obsessions or compulsions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Obsessions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Recurrent thoughts, impulses or images that cause",
"marked anxiety or distress, are experienced as intrusive, go beyond",
"excessive worry about real-life problems, and are not related to",
"another mental disorder (eg, are not limited to thoughts about food in a person",
"with anorexia nervosa).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Compulsions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Ritualistic behaviors or mental acts that are performed",
"in response to an obsession or need to be rigidly carried out. These",
"behaviors are excessive and performed to decrease anxiety or distress",
"or avoid some dreaded event, but they are not realistically connected",
"to those dreaded events.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. The obsessions or compulsions are time-consuming",
"(more than one hour per day), cause clinically significant distress, or",
"interfere with a person's daily routine and occupational or social",
"functioning.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Diagnostic and Statistical Manual of Mental Disorders, 4th Ed, Primary Care Version (DSM-IV-PC). American Psychiatric Association, Washington, DC 1995.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
