var title_f15_1_15390="Photosensitive disorders";
var content_f15_1_15390=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F57800&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F57800&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Photosensitive disorders: Clinical features",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Sex",
"       </td>",
"       <td class=\"subtitle1\">",
"        Age of onset",
"       </td>",
"       <td class=\"subtitle1\">",
"        Timeline of eruption",
"       </td>",
"       <td class=\"subtitle1\">",
"        Location",
"       </td>",
"       <td class=\"subtitle1\">",
"        Common lesion morphology and symptoms",
"       </td>",
"       <td class=\"subtitle1\">",
"        Family history",
"       </td>",
"       <td class=\"subtitle1\">",
"        Action spectrum",
"       </td>",
"       <td class=\"subtitle1\">",
"        Phototesting",
"       </td>",
"       <td class=\"subtitle1\">",
"        Laboratory findings",
"       </td>",
"       <td class=\"subtitle1\">",
"        Duration/ resolution",
"       </td>",
"       <td class=\"subtitle1\">",
"        Key features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Polymorphous light eruption",
"        </strong>",
"       </td>",
"       <td>",
"        F&gt;M",
"       </td>",
"       <td>",
"        First three decades",
"       </td>",
"       <td>",
"        Hours after sun exposure, lasts days to weeks; most conspicuous in spring; improves during summer",
"       </td>",
"       <td>",
"        Sun-exposed areas",
"       </td>",
"       <td>",
"        Pruritic papules, papulovesicles, plaques",
"       </td>",
"       <td>",
"        May be present",
"       </td>",
"       <td>",
"        UVA, UVB, visible",
"       </td>",
"       <td>",
"        Normal MED; provocative phototesting may induce lesions",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Recurrent over the course of years, may improve over time",
"       </td>",
"       <td>",
"        Most common photo-dermatosis; \"hardening\" phenomenon may occur",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Juvenile spring eruption",
"        </strong>",
"       </td>",
"       <td>",
"        M&gt;F",
"       </td>",
"       <td>",
"        Primarily childhood; also may be seen in young adults",
"       </td>",
"       <td>",
"        As with PMLE",
"       </td>",
"       <td>",
"        Classically, helices of ears",
"       </td>",
"       <td>",
"        Erythematous papules, bullae",
"       </td>",
"       <td>",
"        May be present",
"       </td>",
"       <td>",
"        UVA?",
"       </td>",
"       <td>",
"        As with PMLE",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Recurrent over years, may improve with age",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Actinic prurigo",
"        </strong>",
"       </td>",
"       <td>",
"        F&gt;M, M&gt;F reported in adult onset in Asia",
"       </td>",
"       <td>",
"        Childhood",
"       </td>",
"       <td>",
"        Persistent during summer; may also be present year-round",
"       </td>",
"       <td>",
"        Sun-exposed areas, may also affect unexposed areas",
"       </td>",
"       <td>",
"        Pruritic papulonodules, crusts, excoriations, lichenification, cheiltis",
"       </td>",
"       <td>",
"        Yes, in up to 50 percent",
"       </td>",
"       <td>",
"        UVA&gt;UVB",
"       </td>",
"       <td>",
"        60 percent with reduced MED, 60-70 percent with positive provocative phototests",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Improves in adolescence, but also may persist",
"       </td>",
"       <td>",
"        More common in Native Americans. May have ocular findings. HLA-DRB1*0407.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Hydroa vacciniforme",
"        </strong>",
"       </td>",
"       <td>",
"        Slight M&gt;F",
"       </td>",
"       <td>",
"        Childhood",
"       </td>",
"       <td>",
"        Hours after sun exposure",
"       </td>",
"       <td>",
"        Face, dorsal hands",
"       </td>",
"       <td>",
"        Erythematous macules, papules, vesicles, crusts",
"       </td>",
"       <td>",
"        Rarely positive",
"       </td>",
"       <td>",
"        Likely UVA",
"       </td>",
"       <td>",
"        May show reduced MED to UVA in some cases",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Usually resolves by adolescence/young adulthood, but may persist",
"       </td>",
"       <td>",
"        Lympho-proliferative disease association with severe cases",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Chronic actinic dermatitis",
"        </strong>",
"       </td>",
"       <td>",
"        M&gt;F",
"       </td>",
"       <td>",
"        Elderly, but may be seen in younger patients",
"       </td>",
"       <td>",
"        Persistent-worsens in summer, may have findings year-round",
"       </td>",
"       <td>",
"        Sun-exposed areas",
"       </td>",
"       <td>",
"        Eczematous patches, lichenification, may see palmoplantar involvement",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        UVA, UVB, visible light",
"       </td>",
"       <td>",
"        Decreased MEDs to UVA, UVB, or visible light",
"       </td>",
"       <td>",
"        May see S&eacute;zary cells in severe cases",
"       </td>",
"       <td>",
"        Persists for years, may resolve",
"       </td>",
"       <td>",
"        Often coexistent contact dermatitis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Solar urticaria",
"        </strong>",
"       </td>",
"       <td>",
"        F&gt;M",
"       </td>",
"       <td>",
"        Young or mid-adulthood",
"       </td>",
"       <td>",
"        Appears within minutes, individual lesions resolve within 24 hours",
"       </td>",
"       <td>",
"        Sun-exposed areas",
"       </td>",
"       <td>",
"        Urticarial plaques (hives), occasional systemic symptoms",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        UVA, UVB, visible light",
"       </td>",
"       <td>",
"        Normal MEDs, urticaria are induced within minutes of phototesting",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Persists for years, may resolve",
"       </td>",
"       <td>",
"        \"Hardening\" phenomenon may occur",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Phototoxicity",
"        </strong>",
"       </td>",
"       <td>",
"        M = F",
"       </td>",
"       <td>",
"        Any age",
"       </td>",
"       <td>",
"        Appears within hours of sun exposure, can occur after first dose of drug",
"       </td>",
"       <td>",
"        Sun-exposed areas",
"       </td>",
"       <td>",
"        Exacerbated sunburn appearance",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        UVA",
"       </td>",
"       <td>",
"        In systemic phototoxicity, MED for UVA decreased, UVB and visible light MEDs normal",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Resolves when drug discontinued and cleared from body",
"       </td>",
"       <td>",
"        Can occur in anyone",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Photoallergy",
"        </strong>",
"       </td>",
"       <td>",
"        M = F",
"       </td>",
"       <td>",
"        Any age",
"       </td>",
"       <td>",
"        Appears 1-2 days after exposure to sun and inciting agent in sensitized individual",
"       </td>",
"       <td>",
"        Sun-exposed areas",
"       </td>",
"       <td>",
"        Pruritic, eczematous lesions",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        UVA",
"       </td>",
"       <td>",
"        In systemic photoallergy, MED for UVA decreased, UVB and visible light MEDs normal",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Resolves with discontinuation of inciting agent",
"       </td>",
"       <td>",
"        Usually due to topical agents",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Erythropoietic protoporphyria",
"        </strong>",
"       </td>",
"       <td>",
"        M = F, vs slight M&gt;F",
"       </td>",
"       <td>",
"        Onset in early childhood",
"       </td>",
"       <td>",
"        Symptoms begin within minutes of sun exposure, improves in winter",
"       </td>",
"       <td>",
"        Nose, cheeks, hands",
"       </td>",
"       <td>",
"        Burning and stinging, pain, pruritus. Erythema, edema, pupura. Heals with atrophic, wax-like scars; wrinkled knuckles.",
"       </td>",
"       <td>",
"        Common, autosomal dominant",
"       </td>",
"       <td>",
"        Soret band (400-410 nm)",
"       </td>",
"       <td>",
"        Often normal, some patients may note stinging sensation or lesions",
"       </td>",
"       <td>",
"        Elevated erythrocyte protoporphyrin level",
"       </td>",
"       <td>",
"        Chronic",
"       </td>",
"       <td>",
"        May develop liver disease",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Porphyria cutanea tarda",
"        </strong>",
"       </td>",
"       <td>",
"        M = F",
"       </td>",
"       <td>",
"        Middle age, may occur earlier",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Sun-exposed areas",
"       </td>",
"       <td>",
"        Skin fragility, bullae, crusts, scarring. Hypertrichosis, hyperpigmentation, sclerodermoid changes.",
"       </td>",
"       <td>",
"        Sporadic in Type I, autosomal dominant in Type II",
"       </td>",
"       <td>",
"        Soret band (400-410 nm)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Elevated porphyrins in urine and stool",
"       </td>",
"       <td>",
"        Chronic, symptoms improve with sun protection removal of triggers, phlebotomy, antimalarials",
"       </td>",
"       <td>",
"        Often associated with hepatitis C",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
