var title_f15_11_15549="Clinical and laboratory findings in pemphigus";
var content_f15_11_15549=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F87920&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F87920&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Key clinical and laboratory findings in pemphigus",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"9\" width=\"11%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Epidemiology",
"       </td>",
"       <td class=\"subtitle1\">",
"        Autoantigens",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Histopathology",
"       </td>",
"       <td class=\"subtitle1\">",
"        Direct immunofluorescence",
"       </td>",
"       <td class=\"subtitle1\">",
"        Indirect immunofluorescence",
"       </td>",
"       <td class=\"subtitle1\">",
"        ELISA",
"       </td>",
"       <td class=\"subtitle1\">",
"        Variants",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         Pemphigus vulgaris",
"        </strong>",
"       </td>",
"       <td>",
"        Primarily middle-aged adults",
"       </td>",
"       <td>",
"        Desmoglein 1 and desmoglein 3, others",
"       </td>",
"       <td>",
"        Mucosal erosions; flaccid bullae and erosions on skin",
"       </td>",
"       <td>",
"        Suprabasal acantholysis; \"row of tombstones\" pattern of basal keratinocytes",
"       </td>",
"       <td>",
"        Intercellular deposition of IgG",
"       </td>",
"       <td>",
"        Intercellular deposition of IgG; monkey esophagus is preferred substrate",
"       </td>",
"       <td>",
"        Desmoglein 3 or both desmoglein 1 and desmoglein 3 autoantibodies",
"       </td>",
"       <td>",
"        Pemphigus vegetans and pemphigus herpetiformis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         Pemphigus foliaceus",
"        </strong>",
"       </td>",
"       <td>",
"        Primarily middle-aged adults; endemic form most common in children and young adults",
"       </td>",
"       <td>",
"        Desmoglein 1",
"       </td>",
"       <td>",
"        Fragile blisters, shallow erosions, erythematous patches, crusts; mucosal involvement absent",
"       </td>",
"       <td>",
"        Subcorneal or granular layer acantholysis",
"       </td>",
"       <td>",
"        Intercellular deposition of IgG",
"       </td>",
"       <td>",
"        Intercellular deposition of IgG; normal human skin or monkey esophagus is preferred substrate",
"       </td>",
"       <td>",
"        Desmoglein 1 autoantibodies",
"       </td>",
"       <td>",
"        Endemic pemphigus foliaceus (fogo sevalgem), pemphigus erythematosus, and pemphigus herpetiformis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         Paraneoplastic pemphigus",
"        </strong>",
"       </td>",
"       <td>",
"        Primarily adults, but may occur at any age",
"       </td>",
"       <td>",
"        Envoplakin, periplakin, others",
"       </td>",
"       <td>",
"        Severe stomatitis, variable cutaneous findings (eg, blisters, erosions, lichenoid lesions), bronchiolitis obliterans",
"       </td>",
"       <td>",
"        Variable findings; suprabasal acantholysis, keratinocyte necrosis, and lichenoid interface dermatitis are most common",
"       </td>",
"       <td>",
"        Intercellular and/or basement membrane zone deposition of IgG and/or C3",
"       </td>",
"       <td>",
"        Intercellular deposition of IgG; rat bladder is preferred substrate",
"       </td>",
"       <td>",
"        Envoplakin and periplakin autoantibodies",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"9\">",
"        IgA pemphigus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Subcorneal pustular dermatosis-type IgA pemphigus",
"       </td>",
"       <td>",
"        Any age",
"       </td>",
"       <td>",
"        Desmocollin 1",
"       </td>",
"       <td>",
"        Vesicles, pustules, crusts on skin; annular, circinate, or herpetiform morphology; mucosal involvement usually absent",
"       </td>",
"       <td>",
"        Subcorneal clefts and pustules; minimal acantholysis; mixed infiltrate in dermis",
"       </td>",
"       <td>",
"        Intercellular deposition of IgA",
"       </td>",
"       <td>",
"        Intercellular deposits of IgA on monkey esophagus*",
"       </td>",
"       <td>",
"        Desmocollin 1 autoantibodies",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Intraepidermal neutrophilic dermatosis",
"       </td>",
"       <td>",
"        Any age",
"       </td>",
"       <td>",
"        Poorly defined",
"       </td>",
"       <td>",
"        Vesicles, pustules, crusts on skin; annular, circinate, or herpetiform morphology; mucosal involvement usually absent",
"       </td>",
"       <td>",
"        Intraepidermal pustules; minimal acantholysis; mixed infiltrate in dermis",
"       </td>",
"       <td>",
"        Intercellular deposition of IgA",
"       </td>",
"       <td>",
"        Intercellular deposits of IgA on monkey esophagus*",
"       </td>",
"       <td>",
"        Desmoglein 1 and 3 autoantibodies have been reported in some patients",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Indirect immunofluorescence is negative in around 50 percent of patients with IgA pemphigus.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Test availability restricted to specialized laboratories.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
