var title_f15_12_15565="Immune functions of cytokines";
var content_f15_12_15565=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F69910&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F69910&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Contributions of important cytokines to immunologic responses",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Source",
"       </td>",
"       <td class=\"subtitle1\">",
"        Critical functions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        Cytokine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        IL-1",
"       </td>",
"       <td>",
"        Macrophage",
"        <br/>",
"        Many cells",
"       </td>",
"       <td>",
"        Fever, induces other cytokines, T cell stimulation",
"        <br/>",
"        Induces metalloproteinases and prostglandins",
"        <br/>",
"        Increases adhesion molecule expression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        IL-2",
"       </td>",
"       <td>",
"        T cells",
"       </td>",
"       <td>",
"        Increase T cell proliferation, activates B cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        IL-4",
"       </td>",
"       <td>",
"        T cells",
"       </td>",
"       <td>",
"        Signal for immunoglobulin switch, increases IgE",
"        <br/>",
"        Decreases production of proinflammatory cytokines",
"        <br/>",
"        Suppresses delayed type hypersensitivity (Th1 cells)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        IL-6",
"       </td>",
"       <td>",
"        Many cells",
"       </td>",
"       <td>",
"        B and T cell proliferation, acute phase reactants",
"        <br/>",
"        Induces natural protease inhibitor (TIMP)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        IL-12",
"       </td>",
"       <td>",
"        Macrophages",
"       </td>",
"       <td>",
"        Increase interferon-gamma production and Th1 cell differentiation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        IL-13",
"       </td>",
"       <td>",
"        T cells",
"       </td>",
"       <td>",
"        Similar to IL-4",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        IL-15",
"       </td>",
"       <td>",
"        T cells",
"        <br/>",
"        Macrophages",
"       </td>",
"       <td>",
"        Induces TNF-alpha release from synovial macrophages, induces mitogenesis, and inhibits apoptosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        TNF-alpha",
"       </td>",
"       <td>",
"        T cells",
"        <br/>",
"        Macrophages",
"       </td>",
"       <td>",
"        Cachexia, induces other cytokines, T cell stimulation",
"        <br/>",
"        Induces metalloproteinases and prostaglandins",
"        <br/>",
"        Increases adhesion molecule expression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        IFN-gamma",
"       </td>",
"       <td>",
"        T cells",
"       </td>",
"       <td>",
"        Adhesion molecules, HLA-DR expression, activation of T cells, NK cells, macrophages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Colony stimulating factors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        GM-CSF",
"       </td>",
"       <td>",
"        Macrophages",
"        <br/>",
"        Fibroblasts",
"        <br/>",
"        T cells",
"       </td>",
"       <td>",
"        Myeloid differentiation, macrophage activation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        G-CSF",
"       </td>",
"       <td>",
"        Macrophages",
"       </td>",
"       <td>",
"        Granulocyte differentiation and activation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        M-CSF",
"       </td>",
"       <td>",
"        Macrophages",
"       </td>",
"       <td>",
"        Myeloid differentiation, macrophage activation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Chemokines",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        C-X-C family",
"       </td>",
"       <td>",
"        Many cells",
"       </td>",
"       <td>",
"        Activate neutrophils, chemotactic to neutrophil (IL-8, ENA- 78, etc) and T cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        C-C family",
"       </td>",
"       <td>",
"        Many cells",
"       </td>",
"       <td>",
"        Chemotactic for monocytes and T cells (MCP-1, MIP-1, etc)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Growth factors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        TGF-beta",
"       </td>",
"       <td>",
"        Many cells",
"       </td>",
"       <td>",
"        Fibroblast proliferation, collagen and TIMP synthesis",
"        <br/>",
"        Decrease metalloproteinases, decreases T cell proliferation",
"        <br/>",
"        Angiogenesis, decreased pro-inflammatory cytokines",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        FGF, VEGF",
"       </td>",
"       <td>",
"        Many cells",
"       </td>",
"       <td>",
"        Fibroblast proliferation, angiogenesis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        PDGF",
"       </td>",
"       <td>",
"        Many cells",
"       </td>",
"       <td>",
"        Fibroblast proliferation",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
