" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Epidemiology, pathologic features, and diagnosis of classical Hodgkin lymphoma",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/13/15578/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?15/13/15578/contributors\">",
"     Jon C Aster, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/13/15578/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?15/13/15578/contributors\">",
"     Arnold S Freedman, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/13/15578/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?15/13/15578/contributors\">",
"     Rebecca F Connor, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
