var script_f15_14_15599=[" ",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function Framingham08_M_Patient_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.Framingham08_M_Patient_form){",
"",
"",
"doCalc = true;",
"if (Age_param.value.indexOf(',') >= 0){ Age_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Age_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Age_unit.options[Age_unit.selectedIndex].value.split('|');",
"Age = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Systolic_Blood_Pressure_param.value.indexOf(',') >= 0){ Systolic_Blood_Pressure_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Systolic_Blood_Pressure_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Systolic_Blood_Pressure_unit.options[Systolic_Blood_Pressure_unit.selectedIndex].value.split('|');",
"Systolic_Blood_Pressure = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Total_Cholesterol_param.value.indexOf(',') >= 0){ Total_Cholesterol_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Total_Cholesterol_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Total_Cholesterol_unit.options[Total_Cholesterol_unit.selectedIndex].value.split('|');",
"Total_Cholesterol = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (HDL_Cholesterol_param.value.indexOf(',') >= 0){ HDL_Cholesterol_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(HDL_Cholesterol_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = HDL_Cholesterol_unit.options[HDL_Cholesterol_unit.selectedIndex].value.split('|');",
"HDL_Cholesterol = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"Sys_BP_Factor = parseFloat(Sys_BP_Factor_pulldown.options[Sys_BP_Factor_pulldown.selectedIndex].value);",
"Cig = parseFloat(Cig_pulldown.options[Cig_pulldown.selectedIndex].value);",
"DM = parseFloat(DM_pulldown.options[DM_pulldown.selectedIndex].value);",
"dp = 1;",
"Risk_Factors =  (ln(Age) * 3.06117) + (ln(Total_Cholesterol) * 1.12370) - (ln(HDL_Cholesterol) * 0.93263) +  (ln(Systolic_Blood_Pressure) * Sys_BP_Factor) + Cig + DM - 23.9802;",
"",
"Risk =  100 * (1 - power(0.88936, eTo(Risk_Factors)));",
"",
"unit_parts = Risk_unit.options[Risk_unit.selectedIndex].value.split('|');",
"if (doCalc) Risk_param.value = fixDP((Risk - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.Framingham08_M_Patient_form){",
"",
"if (Age_param.value && Age < 19) {",
"Age = 0;",
"Age_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Age is 19 yr.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Age_param.value && Age > 80) {",
"Age_param.value = \"\";",
"clrResults();",
"Age = 0;",
"doCalc = false;",
"alert(\"The maximum value for Age is 80 yr.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.Framingham08_M_Patient_form){",
"",
"Risk_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
""].join("\n");
