var title_f15_17_15644="Changes in pregnancy and heart transplant concerns";
var content_f15_17_15644=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F66004&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F66004&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Normal physiologic changes during pregnancy and associated concerns in the heart transplant recipient",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Physiologic changes",
"       </td>",
"       <td class=\"subtitle1\">",
"        Associated concern in the pregnant heart transplant recipient",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased blood volume",
"       </td>",
"       <td>",
"        Hemodilution may affect serum drug levels.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased heart rate",
"       </td>",
"       <td>",
"        Exacerbation is possible due to cyclosporine. The denervated heart does not respond to the autonomatic nervous system.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased cardiac output",
"       </td>",
"       <td>",
"        In a denervated heart, the cardiac output increases result primarily from volume changes and circulating catecholamines.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased stroke volume",
"       </td>",
"       <td>",
"        Rejection may create systolic/diastolic dysfunction.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Right ventricular enlargement/tricuspid regurgitation",
"       </td>",
"       <td>",
"        In patients requiring serial endomyocardial biopsies there is the potential for worsened tricuspid regurgitation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sodium retention",
"       </td>",
"       <td>",
"        Increased edema; exacerbation of edema by prednisone.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased T-lymphocytes and T-helper cells",
"       </td>",
"       <td>",
"        Increased risk of infection; decreased risk of rejection.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased glucose tolerance",
"       </td>",
"       <td>",
"        Increased risk of diabetogenesis for patients on prednisone.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased gastrointestinal motility and tone",
"       </td>",
"       <td>",
"        Exacerbation of gastrointestinal symptoms with use of immunosuppressants. Exacerbation of reflux due to ulcerogenic effects of chronic steroids.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased risk of urinary tract infections",
"       </td>",
"       <td>",
"        Risk increased secondary to immunosuppressive drugs.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hyperemic/edematous gums",
"       </td>",
"       <td>",
"        Worsened with immunosuppressive agents.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased risk of cholelithiasis",
"       </td>",
"       <td>",
"        Exacerbated with use of azathioprine.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
