" <!-- TC:TOPIC_PAGE -->",
" <div id=\"topicContent\">",
"  <div id=\"drugTitle\">",
"   Bambuterol: International drug information",
"  </div>",
"  <div id=\"lexiTitleImg\">",
"   <img height=\"17\" src=\"./../images/lexiComp/Lexicomp_2012_71x17.myextg\" width=\"71\"/>",
"  </div>",
"  <div class=\"clear\">",
"  </div>",
"  <div id=\"drugCopy\">",
"   Copyright 1978-2013 Lexicomp, Inc. All rights reserved.",
"  </div>",
"  <div id=\"topicText\">",
"   For abbreviations and symbols that may be used in Lexicomp (",
"   <a class=\"graphic graphic_table\" href=\"UTD.htm?23/39/24183\">",
"    show table",
"   </a>",
"   )",
"   <div class=\"list fbnlist drugH1Div drugBrandNames\" id=\"F4324018\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     International Brand Names",
"    </span>",
"    <ul>",
"     <li>",
"      Bambec (AT, DE, DK, ES, GB, HU, IT, LU, NO, SE);",
"     </li>",
"     <li>",
"      Oxeol (FR)",
"     </li>",
"    </ul>",
"   </div>",
"   <div class=\"list synlist drugH1Div drugBrandNames\" id=\"F1977806\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     Index Terms",
"    </span>",
"    <ul>",
"     <li>",
"      Bambuterol Hydrochloride",
"     </li>",
"    </ul>",
"   </div>",
"   <div class=\"list_set htclist drugH1Div drugBrandNames\" id=\"F1977813\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     Pharmacologic Category",
"    </span>",
"    <ul>",
"     <li>",
"      <span class=\"list-set-name\">",
"       Beta",
"       <sub>",
"        2",
"       </sub>",
"       -Adrenergic Agonist",
"      </span>",
"     </li>",
"    </ul>",
"   </div>",
"   <div class=\"block iuse drugH1Div\" id=\"F1977816\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     Reported Use",
"    </span>",
"    <p style=\"text-indent:0em;display:inline\">",
"     Treatment of bronchospasm in bronchial asthma, chronic bronchitis, and emphysema",
"    </p>",
"   </div>",
"   <div class=\"block idos drugH1Div\" id=\"F1977809\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     Dosage Range",
"    </span>",
"    <p style=\"text-indent:0em;display:inline\">",
"     Oral:",
"    </p>",
"    <p style=\"text-indent:-2em;margin-left:2em;text-align:justified;\">",
"     Children 2-5 years: 10 mg/day",
"    </p>",
"    <p style=\"text-indent:-2em;margin-left:2em;text-align:justified;\">",
"     Children 6-12 years: See Adult dose; due to differences in kinetics, doses &gt;10 mg are not recommended in Asian children",
"    </p>",
"    <p style=\"text-indent:-2em;margin-left:2em;text-align:justified;\">",
"     Adults: Initial: 10 mg/day; may be increased to 20 mg/day after 1-2 weeks based on clinical effect. Patients previously treated with oral beta",
"     <sub>",
"      2",
"     </sub>",
"     agonists may be started at 20 mg/day.",
"    </p>",
"    <p style=\"text-indent:-2em;margin-left:2em;text-align:justified;\">",
"     <b>",
"      Dosing adjustment in renal impairment:",
"     </b>",
"     Cl",
"     <sub>",
"      cr",
"     </sub>",
"     &le;50 mL/minute: 5 mg/day",
"    </p>",
"   </div>",
"   <div class=\"block prod-avail drugH1Div\" id=\"F11821080\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     Product Availability",
"    </span>",
"    <p style=\"text-indent:0em;display:inline\">",
"     Product available in various countries; not currently available in the U.S.",
"    </p>",
"   </div>",
"   <div class=\"block foc drugH1Div\" id=\"F1977815\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     Dosage Forms",
"    </span>",
"    <p style=\"text-indent:-2em;margin-left:2em;text-align:justified;\">",
"     Solution, oral: 1 mg/mL [contains sodium benzoate]",
"    </p>",
"    <p style=\"text-indent:-2em;margin-left:2em;text-align:justified;\">",
"     Tablet: 10 mg, 20 mg",
"    </p>",
"   </div>",
"  </div>",
