var title_f15_20_15693="Anthrax vaccine target groups";
var content_f15_20_15693=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F79398&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F79398&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for use of anthrax vaccine absorbed, by type of population",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Population",
"       </td>",
"       <td class=\"subtitle1\">",
"        Pre-event*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Postexposure prophylaxis (PEP)",
"        <span class=\"small\">",
"         &bull;",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         General public",
"        </strong>",
"       </td>",
"       <td>",
"        Not recommended",
"       </td>",
"       <td>",
"        Recommended",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Special populations in the general public",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pregnant women",
"       </td>",
"       <td>",
"        Not recommended",
"       </td>",
"       <td>",
"        Recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Breastfeeding women",
"       </td>",
"       <td>",
"        Not recommended",
"       </td>",
"       <td>",
"        Recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Children (aged &lt;18 yrs)",
"       </td>",
"       <td>",
"        Not recommended",
"       </td>",
"       <td>",
"        Determined on an event-by-event basis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Medical professionals",
"       </td>",
"       <td>",
"        Not recommended",
"       </td>",
"       <td>",
"        Recommended",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Populations at risk for occupational exposure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Persons who handle animals or animal products",
"       </td>",
"       <td>",
"        Not routinely recommended&Delta;",
"       </td>",
"       <td>",
"        Recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Persons who perform certain types of laboratory work",
"       </td>",
"       <td>",
"        Recommended&loz;",
"       </td>",
"       <td>",
"        Based on pre-event vaccination status",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Persons who work in postal facilities",
"       </td>",
"       <td>",
"        Not recommended",
"       </td>",
"       <td>",
"        Recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Military personnel",
"       </td>",
"       <td>",
"        As recommended by the Department of Defense",
"       </td>",
"       <td>",
"        As recommended by the Department of Defense",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Persons involved in environmental investigations or remediation efforts",
"       </td>",
"       <td>",
"        Recommended",
"       </td>",
"       <td>",
"        Based on pre-event vaccination status",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Persons involved in emergency response activities&sect;",
"       </td>",
"       <td>",
"        Not routinely recommended; may be offered on a voluntary basis under the direction of a comprehensive occupational health and safety program",
"       </td>",
"       <td>",
"        Recommended",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Five 0.5-mL doses administered intramuscularly at 0 wks, 4 wks, 6 mos, 12 mos, and 18 mos; annual boosters are required to maintain immunity.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Three 0.5-mL doses administered subcutaneously at 0, 2, and 4 wks after exposure to aerosolized",
"      <em>",
"       Bacillus anthracis",
"      </em>",
"      spores for persons who have not completed the pre-event vaccination schedule.",
"      <br>",
"       &Delta; Recommended only if handling potentially infected animals in research settings or in areas with a high incidence of enzootic anthrax or when standards and restrictions are insufficient to prevent exposure to",
"       <em>",
"        B. anthracis",
"       </em>",
"       spores.",
"       <br>",
"        <span class=\"lozenge\">",
"         &loz;",
"        </span>",
"        Laboratorians who work 1) with pure cultures of",
"        <em>",
"         B. anthracis",
"        </em>",
"        or preparations of",
"        <em>",
"         B. anthracis",
"        </em>",
"        spores, 2) with environmental samples associated with anthrax investigations, or 3) in spore-contaminated areas or other settings with exposure to aerosolized",
"        <em>",
"         B. anthracis",
"        </em>",
"        spores. Laboratorians who do not work in these settings are not recommended for pre-event vaccine.",
"        <br>",
"         &sect; Persons involved in emergency response activities might include persons who work in police departments, fire departments, hazardous material units, and the National Guard, as well as other government responders. These persons might perform site investigations, respond to suspicious substance reports (also known as white powder incidents), and perform other related activities, such as evacuation procedures or other activities critical to the maintenance of infrastructure.",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Modified from: Use of Anthrax Vaccine in the United States: Recommendations of the Advisory Committee on Immunization Practices (ACIP), 2009. MMWR Recomm Rep 2010; 59(RR-6):1-36.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
