"  <div class=\"figure\" style=\"width: 488px\">",
"   <div class=\"ttl\">",
"    Embryonal rhabdomyosarcoma",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 468px; height: 351px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
