var title_f15_21_15707="Animal dander avoidance steps";
var content_f15_21_15707=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F81363&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F81363&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Avoidance measures for animal dander",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Removing animals from the house",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Keep animals outside, eg, in garage or kennel; restricting animals to certain rooms has not been shown to be effective.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Once animal has been removed, the premises should be cleaned thoroughly.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Controlling allergen with an animal in the house",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Difficult because the animal contains 10-50 mg of major allergen, while the quantities of airborne allergen are only 5-20 ng/m3. Using an air filter can only reduce airborne allergen.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Reduce reservoirs: remove carpets, reduce upholstered furniture to a minimum, replace drapes with blinds, or/and vacuum clean weekly using a vacuum with good filtration, ie, double thickness bags and/or HEPA filtration.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Room air filters: HEPA or electrostatic (maintenance data are better defined for HEPA).",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Washing cats does not reduce allergen levels significantly. Washing dogs twice a week may help.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
