var title_f15_21_15708="Functions of medical interview";
var content_f15_21_15708=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F65802&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=15\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F65802&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=15\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Three functions of the medical interview",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Function 1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Data gathering: Determine and monitor the nature of the problem.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Objectives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Enable the clinician to establish a diagnosis or recommend further diagnostic procedures, suggest courses of treatment and predict the nature of the illness.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Function 2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Relationship building: Develop, maintain, and conclude the therapeutic relationship.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Objectives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ensure patient's willingness to provide diagnostic information.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ensure relief of patient's physical and psychological distress.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ensure patient's willingness to accept the treatment plan or a process of negotiation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ensure patient and clinician satisfaction.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Function 3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patient education: Carry out patient education and implementation of treatment plans.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Objectives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ensure patient's understanding of the nature of the illness.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ensure patient's understanding of suggested diagnostic procedures.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Enhance patient's understanding of the treatment possibilities.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Achieve consensus between clinician and patient.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Achieve informed consent.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Improve coping mechanisms.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Promote lifestyle change.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Lipkin M, Putnam S, Lazare A. The Medical Interview, Springer-Verlag, New York 1995.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
