var title_f15_22_15725="Measures RR variability";
var content_f15_22_15725=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F79145&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F79145&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Definitions for time and frequency domain measures of heart period variability",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Variable",
"       </td>",
"       <td class=\"subtitle1\">",
"        Units",
"       </td>",
"       <td class=\"subtitle1\">",
"        Definition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        Time Domain - Statistical measures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Night-day difference",
"       </td>",
"       <td>",
"        ms",
"       </td>",
"       <td>",
"        Difference between the average of all the normal RR intervals at night (24:00 to 05:00) and the average of all the normal RR intervals during the day (07:30 to 21:30).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        SDNN",
"       </td>",
"       <td>",
"        ms",
"       </td>",
"       <td>",
"        Standard deviation of all normal RR intervals in the entire 24-hour ECG recording.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        SDANN",
"       </td>",
"       <td>",
"        ms",
"       </td>",
"       <td>",
"        Standard deviation of the average normal RR intervals for all 288 5-minute segments of a 24-hour ECG recording (each average is weighted by the fraction of the 5 minutes that has normal RR intervals).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        ASDNN",
"       </td>",
"       <td>",
"        ms",
"       </td>",
"       <td>",
"        Average of the standard deviations of normal RR intervals for all 288 5-minute segments of a 24-hour EGC recording.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        r-MSSD",
"       </td>",
"       <td>",
"        ms",
"       </td>",
"       <td>",
"        Root mean square successive difference, the square root of",
"the mean of the squared differences between adjacent normal RR",
"intervals over the entire 24-hour ECG recording.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        pNN50",
"       </td>",
"       <td>",
"        percent",
"       </td>",
"       <td>",
"        Percent of differences between adjacent normal RR intervals",
"that are greater than 50 ms computed over the entire 24-hour ECG",
"recording.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        NN50",
"       </td>",
"       <td>",
"        none",
"       </td>",
"       <td>",
"        Number of adjacent normal RR intervals that are greater than 50 ms counted over the entire 24-hour ECG recording.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        Time Domain - Geometric measures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        HRV triangular index",
"       </td>",
"       <td>",
"        none",
"       </td>",
"       <td>",
"        Total number of NN intervals divided by the number of NN",
"intervals in the modal bin of a histogram of all NN intervals with a",
"bin width of 7.8125 msec (for a sampling rate of 128/sec).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        TINN",
"       </td>",
"       <td>",
"        msec",
"       </td>",
"       <td>",
"        Baseline width of the minimum square difference triangular",
"interpolation of the highest peak of the histogram of all NN intervals.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"        Frequency Domain Measures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Total power",
"       </td>",
"       <td>",
"        ms",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"       <td>",
"        The energy in the heart period power spectrum up to 0.40 Hz.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ultra low frequency (ULF) power",
"       </td>",
"       <td>",
"        ms",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"       <td>",
"        The energy in the heart period power spectrum up to 0.0033 Hz.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Very low frequency (VLF) power",
"       </td>",
"       <td>",
"        ms",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"       <td>",
"        The energy in the heart period power spectrum between 0.0033 and 0.04 Hz.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Low frequency (LF) power",
"       </td>",
"       <td>",
"        ms",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"       <td>",
"        The energy in the heart period power spectrum between 0.04 and 0.15 Hz.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        High frequency (HF) power",
"       </td>",
"       <td>",
"        ms",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"       <td>",
"        The energy in the heart period power spectrum between 0.15 and 0.40 Hz.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        LF/HF ratio",
"       </td>",
"       <td>",
"        none",
"       </td>",
"       <td>",
"        THe ratio of low to high frequency power.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        beta",
"       </td>",
"       <td>",
"        none",
"       </td>",
"       <td>",
"        Slope of log (power) on log (frequency) between 0.01 and 0.0001 Hz on a log-log plot.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
