var title_f15_23_15741="Staging vaginal cancer";
var content_f15_23_15741=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F70622&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F70622&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Staging vaginal cancer (TNM and International Federation of Gynecology and Obstetrics [FIGO])",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Primary tumor (T)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2\">",
"       TNM categories",
"      </td>",
"      <td class=\"subtitle2\">",
"       FIGO stages",
"      </td>",
"      <td class=\"subtitle2\" colspan=\"2\">",
"       Definition",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       TX",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td colspan=\"2\">",
"       Primary tumor cannot be assessed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T0",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td colspan=\"2\">",
"       No evidence of primary tumor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Tis*",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td colspan=\"2\">",
"       Carcinoma in situ (preinvasive carcinoma)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T1",
"      </td>",
"      <td>",
"       I",
"      </td>",
"      <td colspan=\"2\">",
"       Tumor confined to vagina",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T2",
"      </td>",
"      <td>",
"       II",
"      </td>",
"      <td colspan=\"2\">",
"       Tumor invades paravaginal tissues but not to pelvic wall",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T3",
"      </td>",
"      <td>",
"       III",
"      </td>",
"      <td colspan=\"2\">",
"       Tumor extends to pelvic wall&bull;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T4",
"      </td>",
"      <td>",
"       IVA",
"      </td>",
"      <td colspan=\"2\">",
"       Tumor invades mucosa of the bladder or rectum and/or extends beyond the true pelvis (bullous edema is not sufficient evidence to classify a tumor as T4)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Regional lymph nodes (N)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2\">",
"       TNM categories",
"      </td>",
"      <td class=\"subtitle2\">",
"       FIGO stages",
"      </td>",
"      <td class=\"subtitle2\" colspan=\"2\">",
"       Definition",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       NX",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td colspan=\"2\">",
"       Regional lymph nodes cannot be assessed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td colspan=\"2\">",
"       No regional lymph node metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       N1",
"      </td>",
"      <td>",
"       III",
"      </td>",
"      <td colspan=\"2\">",
"       Pelvic or inguinal lymph node metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Distant metastasis (M)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2\">",
"       TNM categories",
"      </td>",
"      <td class=\"subtitle2\">",
"       FIGO stages",
"      </td>",
"      <td class=\"subtitle2\" colspan=\"2\">",
"       Definition",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       M0",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td colspan=\"2\">",
"       No distant metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       M1",
"      </td>",
"      <td>",
"       IVB",
"      </td>",
"      <td colspan=\"2\">",
"       Distant metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Anatomic stage/prognostic groups",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage 0*",
"      </td>",
"      <td>",
"       Tis",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage I",
"      </td>",
"      <td>",
"       T1",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage II",
"      </td>",
"      <td>",
"       T2",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Stage III",
"      </td>",
"      <td>",
"       T1-T3",
"      </td>",
"      <td>",
"       N1",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T3",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage IVA",
"      </td>",
"      <td>",
"       T4",
"      </td>",
"      <td>",
"       Any N",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage IVB",
"      </td>",
"      <td>",
"       Any T",
"      </td>",
"      <td>",
"       Any N",
"      </td>",
"      <td>",
"       M1",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Note: cTNM is the clinical classification, pTNM is the pathologic classification.",
"    <div class=\"footnotes\">",
"     * FIGO no longer includes Stage 0 (Tis).",
"     <br>",
"      &bull; Pelvic wall is defined as muscle, fascia, neurovascular structures, or skeletal portions of the bony pelvis. On rectal examination, there is no cancer-free space between the tumor and pelvic wall.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Used with the permission of the American Joint Committee on Cancer (AJCC), Chicago, Illinois. The original source for this material is the AJCC Cancer Staging Manual, Seventh Edition (2010) published by Springer New York, Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
