" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Cystic fibrosis: Nutritional issues",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/24/15754/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?15/24/15754/contributors\">",
