var title_f15_24_15755="Histologic features DIP";
var content_f15_24_15755=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F59081&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F59081&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Histologic features of desquamative interstitial pneumonia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Key histologic features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Uniform involvement of lung parenchyma.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prominent accumulation of alveolar macrophages (may show fine granular positivity with iron stains).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mild to moderate fibrotic thickening of alveolar septa.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mild interstitial chronic inflammation (lymphoid aggregates).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Pertinent negative findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dense and extensive fibrosis: inconspicuous or absent.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Smooth muscle proliferation: inconspicuous or absent.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Honeycomb fibrosis absent.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fibroblastic foci and organizing pneumonia: inconspicuous or absent.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Eosinophils: inconspicuous, absent, or only focal.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
