var title_f15_28_15821="Rapid overview Epidural hematoma in children";
var content_f15_28_15821=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F69494&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F69494&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Rapid overview: Epidural hematoma (EDH) in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Clinical features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Head trauma, especially to the temporal region",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Possible signs and symptoms include:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        History of loss of consciousness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        History of altered mental status per caregiver",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Severe headache",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Persistent vomiting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Irritability, pallor, cephalohematoma (infants &le;12 months)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Lucid interval followed several hours later by clinical deterioration (classic temporal EDH)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Ataxia, dizziness with potential for abrupt catastrophic deterioration (posterior fossa EDH)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Lateralizing signs (anisocoria, hemiparesis, hemiplegia) and Cushing triad (hypertension, bradycardia, respiratory depression) are preterminal findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Diagnostic evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Primary goal is to make the diagnosis of EDH BEFORE neurologic deterioration in an asymptomatic or mildly affected child to allow for timely surgical intervention",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Determine GCS and identify pupillary abnormalities and/or lateralizing motor findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Initial laboratory studies:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Complete blood count with platelets",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        PT, PTT, INR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Type and cross",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Other studies (eg, AST, ALT, and urinalysis) may be indicated in multiple trauma patients",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Obtain emergent unenhanced head CT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lumbar puncture is contraindicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Obtain neurosurgical consultation for all patients with GCS &le;12 or with identified EDH",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Manage patient according to principles of advanced trauma life support*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Immobilize cervical spine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Treat hypoxemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Assess airway, breathing, circulation, and disability and initiate supportive care",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Endotracheally intubate children with GCS &le;8 or rapidly worsening mental status and ventilate to maintain pCO2 between 35 and 40 mmHg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Provide fluid resuscitation, as needed, to prevent hypotension*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ensure prompt evaluation by a neurosurgeon to determine need for surgical evacuation of the EDH",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        If signs of herniation, provide hyperosmolar therapy (preferably in consultation with a neurosurgeon):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        3 percent hypertonic saline solution in a volume of 2 to 6 mL/kg IV per dose (maximum 300 mL), as a rapid bolus infusion. May repeat if needed or initiate infusion. Closely monitor serum sodium concentration and osmolarity (maintain &lt;360 mOsm/L).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Mannitol 20 percent solution, 0.5 to 1 gram/kg IV per dose, as a rapid bolus infusion. May repeat after 6 hours as needed to increase serum osmolarity (maintain &lt;320 mOsm/L).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perform mild hyperventilation (PaCO2 30-35 mmHg) for children whose signs of herniation do not respond to mannitol",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     EDH: epidural hematoma; PT: prothrombin time; PTT: partial thromboplastin time; INR: international normalized ratio; AST: aspartate transferase; ALT: alanine transferase; CT: computed tomography; GCS: Glasgow coma scale.",
"     <br/>",
"     * See \"Trauma management: Approach to the unstable child\".",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
