"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F10022659\">",
"      General drug facts",
"     </a>",
"    </li>",
"   </ul>",
"  </div>",
"  <h1>",
"   RELATED TOPICS",
"  </h1>",
"  <div id=\"relatedTopics\">",
"   <ul>",
"    <li class=\"plainItem\">",
