var title_f15_29_15836="Frisen scale";
var content_f15_29_15836=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F73887&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F73887&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Modified Fris&eacute;n Scale for grading papilledema",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Stage 0: Normal optic disc or not a disc but no edema/swelling",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A. Prominence of the retinal nerve fiber layer at the nasal, superior and inferior poles in inverse proportion to disc diameter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B. Radial nerve fiber layer striations, without tortuosity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Stage I: Minimal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A. C-shaped halo that is subtle and grayish with a temporal gap; obscures underlying retinal details",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B. Disruption of normal radial NFL arrangement striations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C. Temporal disc margin normal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Stage II: Low degree",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A. Circumferential halo",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B. Elevation - nasal border",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C. No major vessel obscuration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Stage III: Moderate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A. Obscuration of one or more segments of major blood vessels leaving disc",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B. Circumferential halo",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C. Elevation - all borders",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        D. Halo - irregular outer fringe with finger-like extensions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Stage IV: Marked",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A. Total obscuration on the disc of a segment of a major blood vessel on the disc",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        B. Elevation - whole nerve head, including the cup",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C. Border obscuration - complete",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        D. Halo - complete",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Stage V: Severe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A. Partial obscuration of all vessels on disc and total obscuration of at least one vessel on disc",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Fris&eacute;n, L. Swelling of the optic nerve head: a staging scheme. J Neurol Neurosurg Psychiatry 1982; 45:13.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
