" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Fever in children with chemotherapy-induced neutropenia",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/33/15898/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?15/33/15898/contributors\">",
"     Nabil M Ahmed, MD, MPH",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?15/33/15898/contributors\">",
"     Patricia M Flynn, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
