var title_f15_34_15915="NCCN followup guide HNC";
var content_f15_34_15915=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F54259&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F54259&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    National Comprehensive Cancer Network (NCCN) recommended follow-up for head and neck cancers",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Posttreatment year",
"      </td>",
"      <td class=\"subtitle1\">",
"       Physical examination frequency",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1",
"      </td>",
"      <td>",
"       Every one to three months",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2",
"      </td>",
"      <td>",
"       Every two to four months",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3-5",
"      </td>",
"      <td>",
"       Every four to six months",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &gt;5",
"      </td>",
"      <td>",
"       Every six to twelve months",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Frequency of physical examination is the same across head and neck cancer sites: paranasal sinus, salivary gland, lip, oral cavity, oropharynx, larynx, hypopharynx, and nasopharynx.",
"    <br>",
"     Additional screening examinations include: thyroid stimulating hormone (TSH) every 6-12 months, if the neck was irradiated; chest imaging as indicated; speech, hearing, and swallowing evaluation and rehabilitation as indicated; and dental evaluation as indicated.",
"     <div class=\"footnotes\">",
"     </div>",
"     <div class=\"reference\">",
"      Data from: National Comprehensive Cancer Network guidelines. Available online at: file://www.nccn.org/professionals/physician_gls/f_guidelines.asp (Accessed February 26, 2010).",
"     </div>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
