" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 470px\">",
"   <div class=\"ttl\">",
"    Putting on compression stockings",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 421px; height: 437px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
