var title_f15_36_15947="Stool electrolytes";
var content_f15_36_15947=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F51596&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F51596&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Interpretation of stool electrolytes",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"3\">",
"       Calculation of the osmotic gap",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"3\">",
"       Stool osmotic gap = 290 - estimated stool osmolality",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"3\">",
"       Estimated stool osmolality = 2 x ([Na",
"       <sub>",
"        stool",
"       </sub>",
"       + K",
"       <sub>",
"        stool",
"       </sub>",
"       ])",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"3\">",
"       Laboratory tests aiding the differentiation between secretory and osmotic diarrhea",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Laboratory tests",
"      </td>",
"      <td class=\"subtitle2_single\">",
"       Secretory",
"      </td>",
"      <td class=\"subtitle2_single\">",
"       Osmotic",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Osmotic gap",
"      </td>",
"      <td class=\"indent1\">",
"       &lt;50 mOsm/kg",
"      </td>",
"      <td class=\"indent1\">",
"       &gt;125 mOsm/kg",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       pH",
"      </td>",
"      <td class=\"indent1\">",
"       &gt;6.0",
"      </td>",
"      <td class=\"indent1\">",
"       &lt;5.5",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Richard Kellermayer, MD, PhD and Robert J Shulman, MD.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
