" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management and prognosis of cerebral palsy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
