var title_f15_39_15995="Classification SRs to SCIT";
var content_f15_39_15995=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F67718&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F67718&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of systemic reactions to subcutaneous immunotherapy (SCIT)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        AAAAI/ACAAI Surveillance Study",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Grade I &rarr; Mild systemic reactions:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Generalized urticaria and/or upper respiratory symptoms (eg, itching of the palate and throat, sneezing)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Grade 2 &rarr; Moderate systemic reactions:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Asthma (eg, PEFR falls 20 to 40 percent) with or without generalized urticaria, upper respiratory symptoms, or abdominal symptoms (nausea, cramping)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Grade 3 &rarr; Severe life threatening anaphylaxis:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Severe airway compromise due to severe bronchospasm (eg, PEFR falls more than 40 percent) or upper airway obstruction with stridor, and/or hypotension (with or without loss of consciousness)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    AAAAI: American Academy of Allergy, Asthma, and Immunology. ACAAI: American College of Allergy, Asthma, and Immunology",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
