" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical manifestations, diagnosis, and treatment of brucellosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/43/16058/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?15/43/16058/contributors\">",
"     Mile Bosilkovski, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/43/16058/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
