var title_f15_43_16059="Phases lung development";
var content_f15_43_16059=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F72141&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F72141&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Phases of lung development",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Gestational age, weeks",
"       </td>",
"       <td class=\"subtitle1\">",
"        Stage of lung development",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        0 to 5",
"       </td>",
"       <td>",
"        Embryonic",
"       </td>",
"       <td>",
"        Lung bud arises",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        6 to 17",
"       </td>",
"       <td>",
"        Pseudoglandular",
"       </td>",
"       <td>",
"        Nonrespiratory bronchi and bronchioles develop",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        17 to 24",
"       </td>",
"       <td>",
"        Canalicular",
"       </td>",
"       <td>",
"        First gas exchanging acini and pulmonary capillaries are forming",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        25 to 37",
"       </td>",
"       <td>",
"        Terminal sac",
"       </td>",
"       <td>",
"        Subsaccules and alveoli develop with extensive capillary invasion and expansion of the alveolar-blood barrier surface area",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        38 to age 3 years",
"       </td>",
"       <td>",
"        Alveolar",
"       </td>",
"       <td>",
"        Subsaccules become alveoli",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
