" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Oral drugs for treatment of hypertensive urgencies in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Route*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Onset of action",
"       </td>",
"       <td class=\"subtitle1\">",
"        Duration of action",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism of action",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clonidine&bull;",
"       </td>",
"       <td>",
"        Oral",
"       </td>",
"       <td>",
"        0.05-0.1 mg/dose every hour up to 0.8 mg total dose",
"       </td>",
"       <td>",
"        15-30 minutes",
"       </td>",
"       <td>",
"        6-8 hours",
"       </td>",
"       <td>",
"        Central alpha2 adrenergic and imidazoline agonist",
"       </td>",
"       <td>",
"        Effective dose for acute hypertension in infants and toddlers is",
"        <strong>",
"         not",
"        </strong>",
"        known",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hydralazine",
"       </td>",
"       <td>",
"        Oral",
"       </td>",
"       <td>",
"        0.25 mg/kg per dose up to 25 mg maximum single dose",
"       </td>",
"       <td>",
"        30 min",
"       </td>",
"       <td>",
"        2-8 hours",
"       </td>",
"       <td>",
"        Direct vasodilator",
"       </td>",
"       <td>",
"        Variable response",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Isradipine",
"       </td>",
"       <td>",
"        Oral",
"       </td>",
"       <td>",
"        0.05-0.1 mg/kg per dose up to 5 mg maximum single dose",
"       </td>",
"       <td>",
"        &lt;1 hour",
"       </td>",
"       <td>",
"        6-8 hours",
"       </td>",
"       <td>",
"        Calcium channel blocker",
"       </td>",
"       <td>",
"        Stable oral suspension can be compounded",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Minoxidil",
"       </td>",
"       <td>",
"        Oral",
"       </td>",
"       <td>",
"        0.1-0.2 mg/kg per dose up to 10 mg maximum single dose",
"       </td>",
"       <td>",
"        1 hour",
"       </td>",
"       <td>",
"        8-12 hours",
"       </td>",
"       <td>",
"        Direct arterial vasodilator",
"       </td>",
"       <td>",
"        Most potent oral vasodilator with longest duration of action",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
