var title_f15_45_16091="Causes elevated IgE";
var content_f15_45_16091=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F56468&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F56468&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of elevated immunoglobulin E (IgE)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Common*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Allergic disease (eg, atopic dermatitis/eczema, asthma, allergic rhinitis, food allergy, eosinophilic esophagitis, urticaria, drug allergy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Parasitic worm infestation (eg, helminth infection)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Uncommon",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Malignancy (eg, IgE myeloma, Hodgkin's lymphoma)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Primary and secondary immunodeficiencies (eg, Hyper-IgE syndrome, Wiskott-Aldrich syndrome, Nezelof syndrome, AIDS, GVHD)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Infectious diseases (eg, allergic bronchopulmonary aspergillosis, leprosy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inflammatory diseases (eg, Churg-Strauss syndrome, Kawasaki disease)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Drug effect (eg, aztronam, penicillin G)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Allergic disease is the most common etiology of elevated IgE in industrialized countries, whereas parasitic infection is the most common cause in developing countries.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
