var title_f15_45_16092="Classification of FMD";
var content_f15_45_16092=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEPH%2F54161&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEPH%2F54161&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of fibromuscular dysplasia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Classification",
"       </td>",
"       <td class=\"subtitle1\">",
"        Frequency, percent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Pathology",
"       </td>",
"       <td class=\"subtitle1\">",
"        Angiographic appearance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\" rowspan=\"1\">",
"        Medial dysplasia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Medial fibroplasia",
"       </td>",
"       <td>",
"        75-80",
"       </td>",
"       <td>",
"        <p>",
"         Alternating areas of thinned media and thickened fibromuscular ridges containing collagen",
"        </p>",
"        <p>",
"         Internal elastic membrane may be lost in some areas",
"        </p>",
"       </td>",
"       <td>",
"        \"String of Beads\" appearance where the diameter of the \"beading\" is larger than the diameter of the artery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Perimedial fibroplasia",
"       </td>",
"       <td>",
"        10-15",
"       </td>",
"       <td>",
"        Extensive collagen deposition in the outer half of the media",
"       </td>",
"       <td>",
"        \"Beading\" in which the \"beads\" are smaller than the diameter of the artery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Medial hyperplasia",
"       </td>",
"       <td>",
"        1-2",
"       </td>",
"       <td>",
"        True smooth muscle cell hyperplasia without fibrosis",
"       </td>",
"       <td>",
"        Concentric smooth stenosis (similar to intimal disease)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\" rowspan=\"1\">",
"        Intimal fibroplasia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &lt;10",
"       </td>",
"       <td>",
"        <p>",
"         Circumferential or eccentric deposition of collagen in the intima",
"        </p>",
"        <p>",
"         No lipid or inflammatory component",
"        </p>",
"        <p>",
"         Internal elastic lamina fragmented or duplicated",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Concentric focal band",
"        </p>",
"        <p>",
"         Long smooth narrowing",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\" rowspan=\"1\">",
"        Adventitial (periarterial) fibroplasia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &lt;1",
"       </td>",
"       <td>",
"        Dense collagen replacesthe fibrous tissue of theadventia and may extendinto surrounding tissue",
"       </td>",
"       <td>",
"        Sharply localized, tubularareas of stenosis (based onlimited angiographic data)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from: Begelman SM, Olin JW. Fibromuscular dysplasia. Curr Opin Rheumatol 2000; 12:41. Copyright &copy; 2000 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
