var title_f15_46_16107="Preventing lead exposure from paint";
var content_f15_46_16107=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F68214&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F68214&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Educational interventions to prevent exposure to lead in paint",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Clean up loose paint chips immediately",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wipe off loose paint using damp disposable cloths or rags",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Block access to chipping paint with furniture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Put contact paper over chipping paint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hose off porch or place down a blanket or rug where children play",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Seal or enclose areas with small amounts of chipping paint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Do not use hazardous methods of removing paint:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Sanding without a HEPA attachment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Open-flame burning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Chemical removal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use only certified abatement contractors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Make sure family and belongings (eg, upholstered furniture) are protected during abatement",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Environmental Protection Agency. (file://www.epa.gov/lead/educationexesum.htm)",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
