" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Genetic disorders causing surfactant dysfunction",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        SFTPB",
"       </td>",
"       <td class=\"subtitle1\">",
"        SFTPC",
"       </td>",
"       <td class=\"subtitle1\">",
"        ABCA3",
"       </td>",
"       <td class=\"subtitle1\">",
"        NKX2.1&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Mode of inheritance",
"        </strong>",
"       </td>",
"       <td>",
"        Autosomal recessive",
"       </td>",
"       <td>",
"        Autosomal dominant,",
"        <em>",
"         de novo",
"        </em>",
"       </td>",
"       <td>",
"        Autosomal recessive",
"       </td>",
"       <td>",
"        Autosomal dominant,",
"        <em>",
"         de novo",
"        </em>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Pulmonary presentations",
"        </strong>",
"       </td>",
"       <td>",
"        Neonatal respiratory distress syndrome",
"       </td>",
"       <td>",
"        <p>",
"         Childhood interstitial lung disease",
"        </p>",
"        <p>",
"         Adult interstitial lung disease",
"        </p>",
"        <p>",
"         Neonatal respiratory distress syndrome",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Neonatal respiratory distress syndrome",
"        </p>",
"        <p>",
"         Childhood interstitial lung disease",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Neonatal respiratory distress syndrome",
"        </p>",
"        <p>",
"         Childhood interstitial lung disease",
"        </p>",
"        <p>",
"         Recurrent infection",
"        </p>",
"        <p>",
"         No pulmonary involvement",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Course",
"        </strong>",
"       </td>",
"       <td>",
"        Neonatal lethal",
"       </td>",
"       <td>",
"        <p>",
"         Highly variable",
"        </p>",
"        <p>",
