" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Kawasaki disease: Complications",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
