" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Preparations for postmenopausal hormone therapy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/53/16218/contributors\">",
