var title_f15_53_16219="Interventions based on stage";
var content_f15_53_16219=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F65493&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=11\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F65493&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=11\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Effective physician lifestyle interventions based upon patient's stage of change",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        Precontemplation stage",
"       </td>",
"       <td>",
"        Provide background information on medical and personal reasons for change.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        Contemplation stage",
"       </td>",
"       <td>",
"        Support patient's own motivation to make change; develop greater awareness of personal benefits to patient.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        Preparation stage",
"       </td>",
"       <td>",
"        Assist in dealing with issues and barriers; collaborate on developing a specific plan.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        Action stage",
"       </td>",
"       <td>",
"        Follow through with the plan; deal with issues related to",
"adherence to the initiation of change (issues may be present for weeks or months).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        Maintenance stage",
"       </td>",
"       <td>",
"        Continue to emphasize long-term medical benefits and",
"personal motivation; deal with relapses as \"normal\" (reassess patient's",
"Stage of Change and continue to work with patient on change issues).",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
